        // }
        return true;
      }

      /*验证码倒计时*/
      // function time_down(obj){

      // }

      /*获取验证码*/
      var wait = 60;
      function time(o) {
        if ($('.get-code').val() == "<?php echo htmlentities(app('lang')->get('Sendverif')); ?>") {
          $.ajax({
            url: '/index/send/smsyun',
            data: {
              'tel': $("input[name=tel]").val(),
              'dai': $('#dai').val(),
            },
            type: 'POST',
            success: function (data) {
              var res = JSON.parse(data);
              if (res.code == 0) {
                $(document).dialog({ infoText: "<?php echo lang('发送成功'); ?>" });
              } else {
                $(document).dialog({ infoText: "<?php echo lang('发送失败'); ?>" });
              }
            }
          });
        }
        if (wait == 0) {
          o.removeAttribute("disabled");
          o.value = "<?php echo htmlentities(app('lang')->get('Sendverif')); ?>";
          wait = 60;
        } else {

          o.setAttribute("disabled", true);
          o.value = wait;
          wait--;
          setTimeout(function () {
            time(o)
          },
            1000)
        }
      }

      document.getElementById("get-code").onclick = function () { time(this); }

      /*提交*/
      $(".form-buttom").on('click', function () {
